def pulisci_nickname(nickname: str, ban_list_pattern= ban_list) -> str:
    # 1️⃣ Rimuovi le parole della ban list
    nickname = re.sub(ban_list_pattern, "", nickname)

    # 2️⃣ Rimuovi tutti i caratteri non alfabetici (tranne spazio)
    nickname = re.sub(r"[^A-Za-z\s]", " ", nickname)
    nickname = re.sub(r"\breserv(ed)?\b", "", nickname) 
    
    # 3️⃣ Split e rimuovi parole troppo corte
    parole = [w for w in nickname.split() if len(w) > 1]
    
    # 4️⃣ Rimuovi duplicati consecutivi
    risultato = []
    for w in parole:
        if not risultato or risultato[-1].lower() != w.lower():
            risultato.append(w)
    
    # 5️⃣ Controllo metà duplicata
    n = len(risultato)
    if n % 2 == 0 and n > 2:
        metà = n // 2
        prima = risultato[:metà]
        seconda = risultato[metà:]
        if [w.lower() for w in prima] == [w.lower() for w in seconda]:
            risultato = prima
    
    # 6️⃣ Capitalizza e ricostruisci stringa finale
    nickname_pulito = " ".join(w.capitalize() for w in risultato)
    
    return nickname_pulito.strip()